<div class="sidebar-part">	<span class="sidebar-part-underline">		<span class="sidebar-part-title">Category</span>	</span>	<ul class="sidebar-menu">		<?php			$args = array(				'hide_empty' => false,				'order' => 'ASC',				'orderby' => 'menu_order',				'parent' => '0',				'hierarchical' => true			);			$terms_query = get_terms('notecategory', $args);			foreach ( $terms_query as $term ): 			$subterms = get_terms( 'notecategory', array( 'child_of' => $term->term_id ) ); ?>		<li>			<?php if (!empty($subterms)) :?>				<span class="sidebar-menu-toggle"></span><a href="<?php echo get_term_link($term,'notecategory');?>"><?php echo esc_html($term->name); ?>(<?php echo esc_attr($term->count)?>)</a>				<ul class="sidebar-submenu">					<?php foreach ( $subterms as $subterm ): ?>						<li><a href="http://left.tw/notecat/<?php echo $subterm->slug;?>"><?php echo $subterm->name;?>(<?php echo esc_attr($subterm->count)?>)</a></li>					<?php endforeach;?>				</ul>			<?php else :?>				<span class="sidebar-menu-icon"></span><a href="<?php echo get_term_link($term,'notecategory');?>"><?php echo esc_html($term->name); ?>(<?php echo esc_attr($term->count)?>)</a>			<?php endif;?>		</li>		<?php endforeach;wp_reset_query();?>	</ul></div>